import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Paper,
  Button,
  Grid,
} from '@mui/material';

interface ReadingResults {
  totalWords: number;
  duration: number;
  wordsPerMinute: number;
}

const ResultsPage = () => {
  const navigate = useNavigate();
  const [results, setResults] = useState<ReadingResults | null>(null);

  useEffect(() => {
    const savedResults = localStorage.getItem('readingResults');
    if (!savedResults) {
      navigate('/');
      return;
    }
    setResults(JSON.parse(savedResults));
  }, [navigate]);

  const handleTryAgain = () => {
    navigate('/');
  };

  if (!results) {
    return null;
  }

  return (
    <Container maxWidth="sm">
      <Box sx={{ mt: 4, mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom align="center">
          Результати читання
        </Typography>
        <Paper elevation={3} sx={{ p: 3, mt: 3 }}>
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <Typography variant="h6" align="center">
                Швидкість читання: {results.wordsPerMinute} слів за хвилину
              </Typography>
            </Grid>
            <Grid item xs={12}>
              <Typography variant="body1" align="center">
                Всього прочитано: {results.totalWords} слів
              </Typography>
            </Grid>
            <Grid item xs={12}>
              <Typography variant="body1" align="center">
                Час читання: {Math.round(results.duration)} секунд
              </Typography>
            </Grid>
            <Grid item xs={12}>
              <Button
                variant="contained"
                color="primary"
                fullWidth
                size="large"
                onClick={handleTryAgain}
              >
                Спробувати ще раз
              </Button>
            </Grid>
          </Grid>
        </Paper>
      </Box>
    </Container>
  );
};

export default ResultsPage;
