import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Paper,
  Slider,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';

const SettingsPage = () => {
  const navigate = useNavigate();
  const [settings, setSettings] = useState({
    wordsPerMinute: 200,
    fontSize: 24,
    wordsAtOnce: 1,
  });

  const handleStart = () => {
    localStorage.setItem('settings', JSON.stringify(settings));
    navigate('/trainer');
  };

  return (
    <Container maxWidth="sm">
      <Box sx={{ mt: 4, mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom align="center">
          Налаштування
        </Typography>
        <Paper elevation={3} sx={{ p: 3, mt: 3 }}>
          <Box sx={{ mb: 3 }}>
            <Typography gutterBottom>
              Швидкість (слів за хвилину): {settings.wordsPerMinute}
            </Typography>
            <Slider
              value={settings.wordsPerMinute}
              onChange={(_, value) =>
                setSettings({ ...settings, wordsPerMinute: value as number })
              }
              min={100}
              max={1000}
              step={50}
            />
          </Box>

          <Box sx={{ mb: 3 }}>
            <Typography gutterBottom>
              Розмір шрифту: {settings.fontSize}px
            </Typography>
            <Slider
              value={settings.fontSize}
              onChange={(_, value) =>
                setSettings({ ...settings, fontSize: value as number })
              }
              min={16}
              max={48}
              step={2}
            />
          </Box>

          <FormControl fullWidth sx={{ mb: 3 }}>
            <InputLabel>Кількість слів за раз</InputLabel>
            <Select
              value={settings.wordsAtOnce}
              label="Кількість слів за раз"
              onChange={(e) =>
                setSettings({
                  ...settings,
                  wordsAtOnce: e.target.value as number,
                })
              }
            >
              <MenuItem value={1}>1 слово</MenuItem>
              <MenuItem value={2}>2 слова</MenuItem>
              <MenuItem value={3}>3 слова</MenuItem>
            </Select>
          </FormControl>

          <Button
            variant="contained"
            color="primary"
            fullWidth
            size="large"
            onClick={handleStart}
          >
            Почати читання
          </Button>
        </Paper>
      </Box>
    </Container>
  );
};

export default SettingsPage;
