import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Paper,
  Button,
  CircularProgress,
} from '@mui/material';

const TrainerPage = () => {
  const navigate = useNavigate();
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [words, setWords] = useState<string[]>([]);
  const [isPlaying, setIsPlaying] = useState(true);
  const [startTime, setStartTime] = useState(Date.now());
  const [settings, setSettings] = useState({
    wordsPerMinute: 200,
    fontSize: 24,
    wordsAtOnce: 1,
  });

  useEffect(() => {
    const savedText = localStorage.getItem('readingText');
    const savedSettings = localStorage.getItem('settings');

    if (!savedText) {
      navigate('/');
      return;
    }

    if (savedSettings) {
      setSettings(JSON.parse(savedSettings));
    }

    setWords(savedText.split(/\s+/));
    setStartTime(Date.now());
  }, [navigate]);

  const getCurrentWords = useCallback(() => {
    const { wordsAtOnce } = settings;
    return words
      .slice(currentWordIndex, currentWordIndex + wordsAtOnce)
      .join(' ');
  }, [currentWordIndex, words, settings]);

  useEffect(() => {
    if (!isPlaying) return;

    const interval = setInterval(() => {
      if (currentWordIndex >= words.length) {
        setIsPlaying(false);
        const endTime = Date.now();
        const duration = (endTime - startTime) / 1000;
        localStorage.setItem('readingResults', JSON.stringify({
          totalWords: words.length,
          duration,
          wordsPerMinute: Math.round((words.length / duration) * 60),
        }));
        navigate('/results');
        return;
      }

      setCurrentWordIndex((prev) => prev + settings.wordsAtOnce);
    }, (60 / settings.wordsPerMinute) * 1000);

    return () => clearInterval(interval);
  }, [currentWordIndex, words.length, settings, isPlaying, navigate, startTime]);

  const togglePlay = () => {
    setIsPlaying(!isPlaying);
  };

  if (words.length === 0) {
    return <CircularProgress />;
  }

  return (
    <Container maxWidth="md">
      <Box
        sx={{
          mt: 4,
          mb: 4,
          minHeight: '100vh',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <Paper
          elevation={3}
          sx={{
            p: 4,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            width: '100%',
          }}
        >
          <Typography
            variant="h4"
            component="div"
            sx={{
              fontSize: `${settings.fontSize}px`,
              mb: 4,
              minHeight: `${settings.fontSize * 1.5}px`,
            }}
          >
            {getCurrentWords()}
          </Typography>

          <Box sx={{ mt: 4 }}>
            <Button
              variant="contained"
              color={isPlaying ? 'secondary' : 'primary'}
              onClick={togglePlay}
              size="large"
            >
              {isPlaying ? 'Пауза' : 'Продовжити'}
            </Button>
          </Box>

          <Typography sx={{ mt: 2 }}>
            Прогрес: {Math.round((currentWordIndex / words.length) * 100)}%
          </Typography>
        </Paper>
      </Box>
    </Container>
  );
};

export default TrainerPage;
